// AI INFLUENCER BLOG POST GENERATOR
// TODO: Change MAYA.AI to your chosen influencer name

const insights = $('Format Final Report').first().json;
const allPosts = $('Filter Posts & Extract Product Links').all();

const issueDate = new Date().toISOString().split('T')[0];
const weekLabel = insights.top_trends && insights.top_trends[0] ? insights.top_trends.slice(0, 2).join(' & ') : 'Latest Fashion Trends';

// Generate influencer-style intro
const intros = [
  `I've been scanning what your favorite influencers wear, and trust meâ€”this week is ðŸ”¥. ${insights.summary}`,
  `You know I see trends before they hit your feed. This week? ${insights.summary}`
];
const intro = intros[0];

// Extract products
const products = allPosts.slice(0, 5).map((item, i) => ({
  name: ['Oversized Blazer', 'Metallic Boots', 'Leather Bag'][i] || 'Fashion Item',
  brand: 'Designer Brand',
  price: '$89',
  affiliate_url: item.json.shopping_url || item.json.post_url,
  influencer_handle: `@${item.json.author}`,
  image: item.json.image_url
}));

const blogPost = {
  issue_date: issueDate,
  week_label: weekLabel,
  title: `Week ${Math.ceil((new Date() - new Date(new Date().getFullYear(), 0, 1)) / 604800000)}: ${weekLabel}`,
  intro: intro,
  summary: insights.summary,
  trends: insights.top_trends || [],
  products: products,
  voice_script: `This week's top trends: ${(insights.top_trends || []).slice(0, 3).join(', ')}.`
};

// Generate blog page content
const pageContent = `// Blog Post: ${blogPost.title}
// Date: ${blogPost.issue_date}
// Generated by n8n AI Influencer Agent

import React from 'react';

export default function BlogPost() {
  return (
    <div className="max-w-4xl mx-auto px-4 py-12">
      <h1 className="text-4xl font-bold mb-6">${blogPost.title}</h1>
      <p className="text-lg text-purple-600 font-semibold mb-8">
        ${blogPost.intro}
      </p>

      <section className="mb-12">
        <h2 className="text-3xl font-bold mb-6">This Week's Vibe</h2>
        <p className="text-lg text-gray-700">${blogPost.summary}</p>
      </section>

      <section className="mb-12">
        <h2 className="text-3xl font-bold mb-6">Key Trends</h2>
        <ul className="space-y-4">
          ${blogPost.trends.map(t => `<li className="border-l-4 border-purple-500 pl-4">${t}</li>`).join('\n          ')}
        </ul>
      </section>

      <section className="mb-12">
        <h2 className="text-3xl font-bold mb-6">Shop the Looks</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          {/* Products here */}
        </div>
      </section>

      <div className="mt-12 bg-gradient-to-r from-purple-500 to-pink-500 rounded-2xl p-8 text-center text-white">
        <h3 className="text-2xl font-bold mb-4">ðŸ’Œ Get This in Your Inbox</h3>
        <a href="/" className="inline-block bg-white text-purple-600 px-8 py-3 rounded-lg font-semibold">Subscribe Now</a>
      </div>
    </div>
  );
}
`;

return [{ 
  json: { 
    ...blogPost,
    pageContent: pageContent,
    pageFilename: `${blogPost.issue_date}.tsx`,
    jsonFilename: `${blogPost.issue_date}.json`
  } 
}];

