// GENERATE VOICE CHAT CONTEXT
const blogData = $('Generate AI Influencer Blog Post').first().json;
const insights = $('Format Final Report').first().json;

const voiceContext = {
  updated_at: new Date().toISOString(),
  week_label: blogData.week_label,
  issue_date: blogData.issue_date,
  key_talk_points: [
    ...blogData.trends.slice(0, 3),
    `The sentiment this week is ${insights.sentiment}.`,
    `I analyzed ${insights.posts_analyzed} posts.`
  ],
  top_trends: insights.top_trends || [],
  popular_colors: insights.popular_colors || [],
  highlight_products: blogData.products.slice(0, 3),
  voice_script: blogData.voice_script,
  blog_url: `/blog/${blogData.issue_date}`
};

return [{ json: voiceContext }];
