// ü§ñ WORKFLOW CONTROLLER AGENT - Budget + Frequency + Post Limits

const CONFIG = {
  max_posts_per_week: 50,
  max_cost_per_week: 5.00,
  cost_per_post: 0.075,
  min_days_between_runs: 7,
  curated_influencers: ['marianna_hewitt', 'weworewhat', 'songofstyle', 'blaireadiebee', 'chrissyford'],
  posts_per_influencer: 10,
  newsletter_url: 'https://design-link-luxe.lovable.app'
};

const today = new Date();
const lastRunDate = new Date('2025-01-20');
const daysSinceLastRun = Math.floor((today - lastRunDate) / (1000 * 60 * 60 * 24));
const estimatedCost = CONFIG.max_posts_per_week * CONFIG.cost_per_post;

const decision = {
  shouldRun: true,
  checks: {
    frequency: { passed: daysSinceLastRun >= CONFIG.min_days_between_runs },
    budget: { passed: estimatedCost <= CONFIG.max_cost_per_week },
    post_limit: { passed: CONFIG.max_posts_per_week === 50 }
  }
};

if (!Object.values(decision.checks).every(c => c.passed)) decision.shouldRun = false;

console.log(`ü§ñ WORKFLOW CONTROLLER: ${decision.shouldRun ? '‚úÖ RUN' : '‚ùå SKIP'} (Cost: $${estimatedCost}, Days: ${daysSinceLastRun})`);

return [{ json: { ...decision, ...CONFIG, estimatedCost, daysSinceLastRun } }];

const posts = $input.all();
let allPosts = [];
if (posts.length > 0 && posts[0].json) {
  if (Array.isArray(posts[0].json)) allPosts = posts[0].json;
  else if (posts[0].json.data) allPosts = posts[0].json.data;
  else allPosts = posts.map(p => p.json);
}
const transformed = allPosts.map(post => ({
  json: {
    id: post.post_id || post.pk,
    username: post.user_posted,
    caption: post.description || '',
    likes: post.likes || 0,
    comments: post.num_comments || 0,
    hashtags: post.hashtags || [],
    posted_at: post.date_posted,
    image_url: (post.photos && post.photos[0]) || post.thumbnail,
    post_url: post.url,
    shopping_url: post.shopping_url || null
  }
}));
return transformed;

// üõ°Ô∏è CONTENT SAFETY FILTER AGENT - Palestine, Politics, Profanity

const posts = $input.all();
const FORBIDDEN = {
  palestine: ['palestine', 'palestinian', 'gaza', 'westbank', 'west bank', 'freepalestine', 'free palestine', 'from the river', 'from river to sea', 'apartheid', 'occupation', 'zionist', 'zionism', 'idf', 'israel', 'israeli', 'nakba', 'intifada', 'al-aqsa', 'bds', 'üáµüá∏', 'üçâ'],
  political: ['political', 'politics', 'election', 'vote', 'campaign', 'president', 'government', 'activism', 'activist', 'protest', 'boycott', 'movement', 'solidarity', 'resistance', 'humanitarian crisis', 'war', 'conflict', 'ceasefire', 'genocide', 'refugee'],
  profanity: ['fuck', 'fucking', 'fucked', 'fucker', 'shit', 'shitty', 'bullshit', 'damn', 'damned', 'goddamn', 'bitch', 'bitchy', 'ass', 'asshole', 'hell', 'bastard', 'crap', 'piss', 'dick', 'cock', 'pussy', 'whore', 'slut'],
  controversial: ['abortion', 'gun control', 'immigration ban', 'racism', 'sexism', 'hate crime', 'nazi', 'fascist', 'terrorist', 'extremist']
};

function isSafe(post) {
  const text = (post.json.caption + ' ' + (post.json.hashtags || []).join(' ')).toLowerCase();
  for (const [category, terms] of Object.entries(FORBIDDEN)) {
    for (const term of terms) {
      if (text.includes(term.toLowerCase())) return false;
    }
  }
  return true;
}

const safePosts = posts.filter(isSafe);
const filtered = posts.length - safePosts.length;

console.log(`üõ°Ô∏è SAFETY FILTER: ${safePosts.length} safe, ${filtered} filtered`);

return safePosts;

// FILTER & EXTRACT PRODUCT LINKS FROM POSTS
const posts = $input.all();

const qualityPosts = posts.filter(item => item.json.likes > 1000);

const cleaned = qualityPosts.map(item => {
  const post = item.json;

  const urlRegex = /(https?:\/\/[^\s]+)/g;
  const captionUrls = (post.caption.match(urlRegex) || []);

  const shoppingDomains = ['shopmy.us', 'liketoknow.it', 'ltk.app', 'amazon.com', 'zara.com', 'hm.com', 'asos.com', 'nordstrom.com', 'revolve.com', 'shopbop.com'];

  const productLinks = captionUrls.filter(url => {
    return shoppingDomains.some(domain => url.toLowerCase().includes(domain));
  });

  const couponRegex = /(?:code|promo|discount|use)\s*[:"]?\s*([A-Z0-9]{4,15})/gi;
  const coupons = [];
  let match;
  while ((match = couponRegex.exec(post.caption)) !== null) {
    coupons.push(match[1]);
  }

  return {
    id: post.id,
    author: post.username,
    caption: post.caption,
    likes: post.likes,
    comments: post.comments,
    hashtags: Array.isArray(post.hashtags) ? post.hashtags.join(', ') : '',
    engagement_rate: (post.likes + post.comments) / 1000,
    posted_date: new Date(post.posted_at).toLocaleDateString('en-US'),
    image_url: post.image_url,
    post_url: post.post_url,
    product_links: productLinks,
    has_products: productLinks.length > 0 || post.shopping_url !== null,
    shopping_url: post.shopping_url,
    coupon_codes: coupons,
    has_coupons: coupons.length > 0
  };
});

return cleaned.map(post => ({ json: post }));

const posts = $input.all();

const influencers = {};
posts.forEach(item => {
  const author = item.json.author;
  if (!influencers[author]) {
    influencers[author] = [];
  }
  influencers[author].push(item.json);
});

const allText = posts.map(item => {
  const post = item.json;
  return `Post by @${post.author} (${post.likes} likes, ${post.comments} comments):\n${post.caption}\n`;
}).join('\n---\n');

const prompt = `You are a fashion trend analyst writing an engaging weekly newsletter. Analyze these ${posts.length} Instagram posts from ${Object.keys(influencers).length} DIFFERENT fashion influencers and create an exciting, diverse summary.

