// COLLECT ALL GITHUB API RESPONSES FOR TRACKING
const allResponses = $input.all();

console.log(`üìä Received ${allResponses.length} GitHub responses`);

const timestamp = new Date().toISOString();
const trackingEntries = [];

// Process each GitHub API response
for (const response of allResponses) {
  const data = response.json;

  // Extract week number from file path
  const weekNumber = data.content?.path?.match(/week-(\d+)/)?.[1] || 'unknown';

  // Determine file type from path
  let fileType = 'unknown';
  let fileName = 'unknown';

  if (data.content?.path?.includes('blog.html')) {
    fileType = 'blog';
    fileName = 'blog.html';
  } else if (data.content?.path?.includes('instagram-script.txt')) {
    fileType = 'instagram';
    fileName = 'instagram-script.txt';
  } else if (data.content?.path?.includes('tiktok-script.txt')) {
    fileType = 'tiktok';
    fileName = 'tiktok-script.txt';
  }

  trackingEntries.push({
    timestamp: timestamp,
    week: weekNumber,
    file_type: fileType,
    file_name: fileName,
    github_url: data.content?.html_url || 'N/A',
    commit_sha: data.commit?.sha || 'N/A',
    status: data.commit ? 'success' : 'failed'
  });
}

console.log(`üìä TRACKING: Logged ${trackingEntries.length} files`);

return trackingEntries.map(entry => ({ json: entry }));

// ü§ñ GENERATE Q&A PROMPT FOR DEVI CHATBOT
