const data = $input.first().json.body;

// Extract email and name from form
const email = (data.email || '').trim().toLowerCase();
const name = (data.name || '').trim();

// Basic email validation
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
const isValidEmail = emailRegex.test(email);

if (!isValidEmail) {
  return [{
    json: {
      error: true,
      message: 'Invalid email address'
    }
  }];
}

// Check if email is from a disposable email provider (optional)
const disposableDomains = ['tempmail.com', '10minutemail.com', 'guerrillamail.com'];
const emailDomain = email.split('@')[1];
const isDisposable = disposableDomains.includes(emailDomain);

if (isDisposable) {
  return [{
    json: {
      error: true,
      message: 'Please use a permanent email address'
    }
  }];
}

// Prepare subscriber data
return [{
  json: {
    email: email,
    name: name || email.split('@')[0],
    status: 'active',
    signup_date: new Date().toISOString().split('T')[0],
    signup_timestamp: new Date().toISOString(),
    source: 'website_form',
    ip_address: $input.first().json.headers['x-forwarded-for'] || $input.first().json.headers['x-real-ip'] || 'unknown'
  }
