// EXTRACT EMAIL FROM URL QUERY PARAMETER
const query = $input.item.json.query;
const email = query.email?.trim().toLowerCase();

// Validate email exists
if (!email) {
  return [{
    json: {
      error: true,
      message: 'No email provided',
      email: null
    }
  }];
}

// Validate email format
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
if (!emailRegex.test(email)) {
  return [{
    json: {
      error: true,
      message: 'Invalid email format',
      email: email
    }
  }];
}

return [{
  json: {
    error: false,
    email: email,
    unsubscribe_date: new Date().toISOString().split('T')[0],
    unsubscribe_timestamp: new Date().toISOString()
  }
}];

// FIND THE SUBSCRIBER ROW TO UPDATE
const targetEmail = $('Extract & Validate Email1').first().json.email;
const allSubscribers = $input.all();

// Find the subscriber with matching email
const subscriberIndex = allSubscribers.findIndex(item => {
  return item.json.email && item.json.email.toLowerCase() === targetEmail.toLowerCase();
});

if (subscriberIndex === -1) {
  return [{
    json: {
      found: false,
      message: 'Email not found in subscriber list',
      email: targetEmail
    }
  }];
}

// Get the row number (add 2 because: 1 for header, 1 for 0-index)
const rowNumber = subscriberIndex + 2;
const subscriber = allSubscribers[subscriberIndex].json;

return [{
  json: {
    found: true,
    email: targetEmail,
    rowNumber: rowNumber,
    currentStatus: subscriber.status,
    name: subscriber.name
  }
}];
