// PARSE BRIGHT DATA RESPONSE
const posts = $input.all();

let allPosts = [];

if (posts.length > 0 && posts[0].json) {
  if (Array.isArray(posts[0].json)) {
    allPosts = posts[0].json;
  } else if (posts[0].json.data && Array.isArray(posts[0].json.data)) {
    allPosts = posts[0].json.data;
  } else {
    allPosts = posts.map(p => p.json);
  }
}

const transformed = allPosts.map(post => ({
  json: {
    id: post.post_id || post.pk,
    username: post.user_posted,
    caption: post.description || '',
    likes: post.likes || 0,
    comments: post.num_comments || 0,
    hashtags: post.hashtags || [],
    posted_at: post.date_posted,
    image_url: post.photos && post.photos[0] ? post.photos[0] : post.thumbnail,
    post_url: post.url,
    engagement_rate: (post.likes + post.num_comments) / 1000
  }
}));

return transformed;

// FILTER & CLEAN INSTAGRAM DATA
const posts = $input.all();

const qualityPosts = posts.filter(item => item.json.likes > 1000);

const cleaned = qualityPosts.map(item => {
  const post = item.json;
  return {
    id: post.id,
    author: post.username,
    caption: post.caption,
    likes: post.likes,
    comments: post.comments,
    hashtags: Array.isArray(post.hashtags) ? post.hashtags.join(', ') : '',
    engagement_rate: post.engagement_rate,
    posted_date: new Date(post.posted_at).toLocaleDateString('en-US'),
    image_url: post.image_url,
    post_url: post.post_url
  };
});

return cleaned.map(post => ({ json: post }));

// PREPARE DATA FOR AI ANALYSIS
const posts = $input.all();

const allText = posts.map(item => {
  const post = item.json;
  return `Post by @${post.author} (${post.likes} likes, ${post.comments} comments):\n${post.caption}\n`;
}).join('\n---\n');

const prompt = `You are a fashion trend analyst. Analyze these Instagram posts and provide insights in JSON format.

Instagram Posts:
${allText}

Provide analysis in this EXACT JSON format:
{
  "top_trends": ["trend 1", "trend 2", "trend 3", "trend 4", "trend 5"],
  "popular_colors": ["color 1", "color 2", "color 3"],
  "popular_styles": ["style 1", "style 2", "style 3"],
  "rising_hashtags": ["#hashtag1", "#hashtag2", "#hashtag3"],
  "key_brands": ["brand 1", "brand 2"],
  "sentiment": "positive/neutral/negative",
  "summary": "2-3 sentence summary of current fashion trends",
  "recommendations": ["recommendation 1", "recommendation 2", "recommendation 3"]
}

Be specific and actionable. Focus on actual trends mentioned in the posts.`;

return [{
  json: {
    prompt: prompt,
    post_count: posts.length,
    total_engagement: posts.reduce((sum, item) => sum + item.json.likes + item.json.comments, 0)
  }
}];

// PARSE & FORMAT FINAL INSIGHTS
const aiResponse = $json.ai_response;
const postCount = $json.posts_analyzed;

let insights;
try {
  const cleanResponse = aiResponse.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
  insights = JSON.parse(cleanResponse);
} catch (e) {
  insights = {
    error: 'Failed to parse AI response',
    raw_response: aiResponse.substring(0, 500)
  };
}

const report = {
  report_title: `Instagram Fashion Insights - ${new Date().toLocaleDateString('en-US', { month: 'long', day: 'numeric', year: 'numeric' })}`,
  posts_analyzed: postCount,
  analysis_date: new Date().toISOString(),
  top_trends: insights.top_trends || [],
  popular_colors: insights.popular_colors || [],
  popular_styles: insights.popular_styles || [],
  rising_hashtags: insights.rising_hashtags || [],
  key_brands: insights.key_brands || [],
  sentiment: insights.sentiment || 'neutral',
  summary: insights.summary || 'No summary available',
  recommendations: insights.recommendations || [],
  next_steps: [
    'Monitor these trending hashtags daily',
    'Consider collaborations with mentioned brands',
    'Create content featuring popular colors',
    'Engage with high-performing posts'
  ]
};

return [{ json: report }];
